codon_table = {
    'UUU':'F','UUC':'F','UUA':'L','UUG':'L',
    'UCU':'S','UCC':'S','UCA':'S','UCG':'S',
    'UAU':'Y','UAC':'Y','UAA':'Stop','UAG':'Stop',
    'UGU':'C','UGC':'C','UGA':'Stop','UGG':'W',
    'CUU':'L','CUC':'L','CUA':'L','CUG':'L',
    'CCU':'P','CCC':'P','CCA':'P','CCG':'P',
    'CAU':'H','CAC':'H','CAA':'Q','CAG':'Q',
    'CGU':'R','CGC':'R','CGA':'R','CGG':'R',
    'AUU':'I','AUC':'I','AUA':'I','AUG':'M',
    'ACU':'T','ACC':'T','ACA':'T','ACG':'T',
    'AAU':'N','AAC':'N','AAA':'K','AAG':'K',
    'AGU':'S','AGC':'S','AGA':'R','AGG':'R',
    'GUU':'V','GUC':'V','GUA':'V','GUG':'V',
    'GCU':'A','GCC':'A','GCA':'A','GCG':'A',
    'GAU':'D','GAC':'D','GAA':'E','GAG':'E',
    'GGU':'G','GGC':'G','GGA':'G','GGG':'G'
}

def translate_rna(rna):
    protein = ""
    for i in range(0, len(rna), 3):
        #returns the number of characters (length) in the string rna.
        codon = rna[i:i+3]
        #Start at position i in the string rna. Take characters up to but not including position i+3.

        if len(codon) < 3:
            break

#Looks up the amino acid (aa) that corresponds to the current codon (3 RNA bases) using the codon table dictionary.
#Example: if codon = "AUG", then aa = "M".

        aa = codon_table[codon]
        if aa == "Stop":
            break
        protein += aa
    return protein

# Example usage
rna = ""
print(translate_rna(rna))
